import React, { Component } from "react";
import Autosuggest from "react-autosuggest";

const escapeRegexCharacters = str => {
  return str.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
};

const getSuggestions = (value, creanciers) => {
  const escapedValue = escapeRegexCharacters(value.trim());
  if (escapedValue === "") {
    return [];
  }
  const regex = new RegExp("^" + escapedValue, "i");
  return creanciers.filter(creancier =>
    regex.test(creancier.denomination_sociale)
  );
};

const getSuggestionValue = suggestion => {
  return suggestion.denomination_sociale;
};

const renderSuggestion = suggestion => {
  return <span>{suggestion.denomination_sociale}</span>;
};

class Autosuggestcreanciers extends Component {
  state = {
    value: "",
    suggestions: [],
    creanciers: []
  };

  setTheSuggestionState = () => {
    this.setState({
      creanciers: this.props.creanciers
    });
  };

  onChange = (event, { newValue, method }) => {
    this.setState({
      value: newValue
    });
  };

  onSuggestionsFetchRequested = ({ value }) => {
    const creanciers = this.props.creanciers;
    this.setState({
      suggestions: getSuggestions(value, creanciers)
    });
  };

  onSuggestionsClearRequested = () => {
    this.setState({
      suggestions: []
    });
  };

  render() {
    const { value, suggestions } = this.state;
    const inputProps = {
      placeholder: "Entrer le nom d'un créancier",
      value,
      onFocus: this.setTheSuggestionState,
      onChange: this.onChange
    };

    return (
      <Autosuggest
        suggestions={suggestions}
        onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
        onSuggestionsClearRequested={this.onSuggestionsClearRequested}
        getSuggestionValue={getSuggestionValue}
        renderSuggestion={renderSuggestion}
        inputProps={inputProps}
      />
    );
  }
}

export default Autosuggestcreanciers;
