import React, { Component } from "react";
import "./formulairecreancier.css";
// import { throws } from "assert";
import axios from "axios";

class Formulairecreancier extends Component {
  state = {
    denomination_sociale: "",
    forme_juridique: "",
    nationalite_societe: "",
    adresse_siege: "",
    code_postal_siege: "",
    ville_siege: "",
    pays_siege: "",
    ville_rcs: "",
    num_rcs: "",
    num_CCIAA: "",
    num_reg_soc: "",
    num_cod_fisc_tva: "",
    capital_social: "",
    nom: "",
    prenom: "",
    civilite: "M.",
    fonction: "",
    active: true
  };

  handleMyUserInputs = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  handleCivilite = e => {
    this.setState({
      civilite: e.target.value
    });
  };

  handleSubmit = () => {
    const monState = { ...this.state };
    console.log(`Pouet: ${Object.keys(monState)}`);
    axios
      .post("http://localhost:4848/api/creanciers", {
        monState

        // denomination_sociale: this.state.denomination_sociale,
        // forme_juridique: this.state.forme_juridique,
        // nationalite_societe: this.state.nationalite_societe,
        // adresse_siege: this.state.adresse_siege,
        // code_postal_siege: this.state.code_postal_siege,
        // ville_siege: this.state.ville_siege,
        // pays_siege: this.state.pays_siege,
        // ville_rcs: this.state.ville_rcs,
        // num_rcs: this.state.num_rcs,
        // num_CCIAA: this.state.num_CCIAA,
        // num_reg_soc: this.state.num_reg_soc,
        // num_cod_fisc_tva: this.state.num_cod_fisc_tva,
        // capital_social: this.state.capital_social,
        // nom: this.state.nom,
        // prenom: this.state.prenom,
        // civilite: this.state.civilite,
        // fonction: this.state.fonction,
        // active: true
      })
      .then(function(response) {
        console.log(response);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  render() {
    return (
      <div>
        <div className="title_créancier pl4">
          <h1 className="titre1">Informations sur les créanciers</h1>
          <h2 className="compagnietitre">
            Compagnie {this.state.denomination_sociale}
          </h2>
        </div>

        {/* Formulaire */}
        <div className="fl w-50">
          <article className="pa4 black-80">
            <form action="submit" method="post" acceptCharset="utf-8">
              <fieldset className="ba b--transparent ph0 mh0 mh2">
                <div className="mt3">
                  <label className="db fw4 lh-copy f6">
                    Dénomination sociale
                  </label>
                  <input
                    className="pa2 input-reset ba bg-transparent w-100 measure"
                    name="denomination_sociale"
                    onChange={this.handleMyUserInputs}
                  />
                </div>
                <div className="mt3">
                  <label className="db fw4 lh-copy f6">Forme juridique</label>
                  <input
                    className="pa2 input-reset ba bg-transparent w-100 measure"
                    name="forme_juridique"
                    onChange={this.handleMyUserInputs}
                  />
                </div>
                <div className="mt3">
                  <label className="db fw4 lh-copy f6">
                    Nationalité de la société
                  </label>
                  <input
                    className="pa2 input-reset ba bg-transparent w-100 measure"
                    name="nationalite_societe"
                    onChange={this.handleMyUserInputs}
                  />
                </div>
                <div className="mt3">
                  <label className="db fw4 lh-copy f6">
                    Adresse du siège social
                  </label>
                  <input
                    className="pa2 input-reset ba bg-transparent w-100 measure"
                    name="adresse_siege"
                    onChange={this.handleMyUserInputs}
                  />
                </div>
                <div className="mt3">
                  <label className="db fw4 lh-copy f6">
                    Code postal du siège social
                  </label>
                  <input
                    className="pa2 input-reset ba bg-transparent w-100 measure"
                    name="code_postal_siege"
                    onChange={this.handleMyUserInputs}
                  />
                </div>
                <div className="mt3">
                  <label className="db fw4 lh-copy f6">
                    Ville du siège social
                  </label>
                  <input
                    className="pa2 input-reset ba bg-transparent w-100 measure"
                    name="ville_siege"
                    onChange={this.handleMyUserInputs}
                  />
                  <div className="mt3">
                    <label className="db fw4 lh-copy f6">
                      Pays du siège social
                    </label>
                    <input
                      className="pa2 input-reset ba bg-transparent w-100 measure"
                      name="pays_siege"
                      onChange={this.handleMyUserInputs}
                    />
                  </div>
                  <div className="mt3">
                    <label className="db fw4 lh-copy f6">Numéro Reg Soc</label>
                    <input
                      className="pa2 input-reset ba bg-transparent w-100 measure"
                      name="num_reg_soc"
                      onChange={this.handleMyUserInputs}
                    />
                  </div>
                  <div className="mt3">
                    <label className="db fw4 lh-copy f6">Numéro CCIAA</label>
                    <input
                      className="pa2 input-reset ba bg-transparent w-100 measure"
                      name="num_CCIAA"
                      onChange={this.handleMyUserInputs}
                    />
                  </div>
                </div>
              </fieldset>
            </form>
          </article>
        </div>
        <div className="fl w-50 ">
          <article className="pa4 black-80">
            <fieldset id="submit" className="ba b--transparent ph0 mh0 ">
              <div className="mt3">
                <label className="db fw4 lh-copy f6">
                  Numéro Code Fisc TVA
                </label>
                <input
                  className="pa2 input-reset ba bg-transparent w-100 measure"
                  name="num_cod_fisc_tva"
                  onChange={this.handleMyUserInputs}
