                  <th>Créancier</th>
                  <th>Débiteur</th>
                  <th>Action</th>
                  <th>Créée le</th>
                  <th>Sélection</th>
                </tr>
              </thead>
              <tbody className="lh-copy">
                {myDebiteurs
                  .sort((a, b) => b.id - a.id)
                  .slice(0, 10)
                  .map(debiteur => {
                    return (
                      <tr
