    let myDebiteur = this.state.debiteursFiltered.filter(d =>
      d.denomination_sociale.includes(name)
    );
    if (myDebiteur[0].id !== undefined) {
      this.setState({
        idDebiteurSelected: myDebiteur[0].id
      });
    }
  };

